import React, {Component} from "react";
import ReactTable from "react-table";
import "react-table/react-table.css";
import {SERVER_URL} from "../constants.js";
import "react-confirm-alert/src/react-confirm-alert.css";

import {MDBBtn, MDBIcon, MDBFormInline} from "mdbreact";
import Snackbar from "@material-ui/core/Snackbar";

import NewPlayList from "./NewBeer";
import Beer from "./Beer";

class BeerList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: this.props.username,
            role: this.props.role,
            image: "data:image/jpeg;base64,",
            beers: [],
            open: false,
            message: "",
            duration: 0
        };
    }

    componentDidMount() {
        this.fetchBeers();
    }

    fetchBeers = async () => {
        this.setState({
            beers: []
        });

        await fetch(SERVER_URL + "beers", {
            method: 'GET',
        })
            .then(response => response.json())
            .then(responseData => {
                this.setState({
                    beers: responseData._embedded.beers,
                });
            })
            .catch(err => console.error(err));
    };

    getBeer = (link) => {
        console.log(link);
        const token = sessionStorage.getItem("jwt");
        fetch(`${link}`, {
                method: "GET",
                headers: {Authorization: token}
            }
        )
            .then(response => response.json())
            .then(responseData => {
                this.setState({
                    beer: responseData._embedded.beers
                });
            })
            .catch(err => console.error(err));
    };

    addProduct(beer) {
        const token = sessionStorage.getItem("jwt");
        fetch(SERVER_URL + "beers", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: token
            },
            body: JSON.stringify(beer)
        })
            .then(res => this.fetchBeers())
            .catch(err => console.error(err));
    }

    updatePlaylist(user, link) {
        const token = sessionStorage.getItem("jwt");
        fetch(link, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json",
                Authorization: token
            },
            body: JSON.stringify(user)
        })
            .then(res => this.setState({open: true, message: "Changes saved"}))
            .catch(err =>
                this.setState({open: true, message: "Error when saving"})
            );
    }

    renderEditable = cellInfo => {
        return (
            <div
                style={{backgroundColor: "#FAFAFA"}}
                contentEditable
                suppressContentEditableWarning
                onBlur={e => {
                    const data = [...this.state.beers];
                    data[cellInfo.index][cellInfo.column.id] = e.target.innerHTML;
                    this.setState({beers: data});
                }}
                dangerouslySetInnerHTML={{
                    __html: this.state.beers[cellInfo.index][cellInfo.column.id]
                }}
            />
        );
    };

    handleClose = (event, reason) => {
        this.setState({open: false});
    };


    render() {
        const columns = [
            {
                Header: "Name",
                accessor: "name",
                Cell: this.renderEditable
            },
            {
                Header: "Brewery",
                accessor: "breweryName",
                Cell: this.renderEditable
            },
            {
                Header: "Country",
                accessor: "country",
                Cell: this.renderEditable
            },
            {
                Header: "Description",
                accessor: "description",
                Cell: this.renderEditable
            },
            {
                Header: " Alcohol by volume",
                accessor: "alcoholByVolume",
                Cell: this.renderEditable

            },
            {
                Header: "Style",
                accessor: "beerStyle",
                Cell: this.renderEditable
            }, {
                Header: "Deleted",
                accessor: "deleted",
                Cell: this.renderEditable
            },
            {
                Header: "Creator",
                accessor: "creator"
            },
            {
                Header: "Image",
                accessor: "_links.image.href",
                sortable: false,
                filterable: false,
                width: 78,
                Cell: ({value}) => (
                    <div>
                        <img alt="" src={value} width="60" height="60"/>
                    </div>
                )
            },
            {
                Header: "Save",
                id: "savebutton",
                sortable: false,
                filterable: false,
                width: 80,
                accessor: "_links.self.href",
                Cell: ({value, row}) => (
                    <MDBBtn
                        color="primary"
                        size="sm"
                        onClick={() => {
                            this.updatePlaylist(row, value);
                        }}
                    >
                        <MDBIcon icon="marker" size="2x" className="white-text"/>
                    </MDBBtn>
                )
            }, {
                Header: "Info",
                sortable: false,
                filterable: false,
                accessor: "name",
                id: 'links',
                Cell: () => (
                    <div>
                        {moreinfo}
                    </div>
                )
            }
        ];

        const NewBeer =
            this.props.role === "ANONYMOUS" ? (
                <div/>
            ) : (
                <NewPlayList
                    className="align-middle"
                    username={this.state.username}
                    addProduct={this.addProduct}
                    fetchBeers={this.fetchBeers}
                />
            );

        const moreinfo =
            this.props.role === "ANONYMOUS" ? (
                <div/>
            ) : (
                <Beer
                    className="align-middle"
                    username={this.state.username}
                    getBeer={this.getBeer}
                />
            );


        return (
            <div className="App">
                <MDBFormInline>{NewBeer}</MDBFormInline>

                <ReactTable
                    auto
                    className="align-middle react-table"
                    data={this.state.beers}
                    columns={columns}
                    filterable={true}
                    defaultFilterMethod={this.customFilter}
                    pageSize={5}
                    noDataText={
                        <div className="spinner-border fast text-info" role="status">
                            <span className="sr-only">Loading...</span>
                        </div>
                    }
                    defaultSorted={[
                        {
                            id: "avgrank",
                            desc: true
                        }
                    ]}
                    showPaginationBottom
                />
                <Snackbar
                    open={this.state.open}
                    onClose={this.handleClose}
                    autoHideDuration={1500}
                    message={this.state.message}
                />
            </div>
        );
    }
}

export default BeerList;
